import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Route, Router } from '@angular/router';
import Posao from '../models/posao';

@Component({
  selector: 'app-odrzavanje',
  templateUrl: './odrzavanje.component.html',
  styleUrls: ['./odrzavanje.component.css']
})
export class OdrzavanjeComponent  implements OnInit{
  constructor(private servis:UserService,private ruter:Router){}

  zavrseni:Posao[]=[]

  cekaju:Posao[]=[]

  vlasnik:string=''



  ngOnInit(): void {
    let naziv=localStorage.getItem('ulogovan')
    this.vlasnik=naziv==null?'':naziv
      this.servis.PosaoZavrseno(this.vlasnik).subscribe(
        d=>{
          this.zavrseni=d
        }
      )

      this.servis.posaoZahtev(this.vlasnik).subscribe(
        c=>{
          this.cekaju=c

          this.servis.odrzavanje(this.vlasnik).subscribe(
            o=>{
              o.forEach(element => {
                  this.cekaju.push(element)
              });
            }
          )
        }
      )   
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>

</head>
<body>
    <header>
        <h1>
            Dobrodošli!
        </h1>
        <nav>
            <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/vlasnik">Profil</a></li>
                <li><a href="/firme">Firme</a></li>
                <li><a href="/zakazivanja">Zakazivanja</a></li>
                <li><a href="/odrzavanje">Odrzavanja</a></li>
            </ul>
        </nav>
    </header>

    Aktuelni poslovi
    <table>
        <tr>
            <th>
                Naziv
            </th>
            <th>
                Datum zavrsetka
            </th>
            <th>
                Tip
            </th>
            <th>
                Broj basta/fontana
            </th>     
        </tr>
        <tr *ngFor="let c of cekaju">
            <td>
                {{c.naziv}}
            </td>
            <td>
                {{c.datumKraja}}
            </td>
            <td>
                {{c.tip}}
            </td>
            <td>
                <div *ngIf="c.tip=='privatna'">
                    b:{{c.brojBazena}}
                </div>
                <div *ngIf="c.tip=='restoran'">
                    f:{{c.brojFontana}}
                </div>
            </td>
        </tr>
    </table>







    <footer>
        <p>&copy; 2024 Vaša bašta.Sva prava zadržana.</p>
    </footer>
</body>
