import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import Posao from '../models/posao';
import Dekorater from '../models/dekorater';

@Component({
  selector: 'app-dekorater-odrzavanja',
  templateUrl: './dekorater-odrzavanja.component.html',
  styleUrls: ['./dekorater-odrzavanja.component.css']
})
export class DekoraterOdrzavanjaComponent implements OnInit {

  constructor(private servis:UserService,private ruter:Router){}


  posloviZahtev:Posao[]=[]

  dekorater:Dekorater=new Dekorater()

  posloviDekorater:Posao[]=[]

  komentar:string=''
  error=''

  ngOnInit(): void {

    let naziv=localStorage.getItem('ulogovan')
    naziv=naziv==null?'':naziv
    this.servis.getDekorater(naziv).subscribe(
      d=>{
        this.dekorater=d

        this.servis.posloviFirmeZahtev(this.dekorater.firma).subscribe(
          p=>{
            this.posloviZahtev=p
          }
        )

        this.servis.dekoraterPoslovi(this.dekorater.korisnicko_ime).subscribe(
          p=>{
            this.posloviDekorater=p
          }
        )
      }
    )

  }

  prihvati(posao:Posao){
    this.servis.dekoraterPrihvatiZahtev(this.dekorater.korisnicko_ime,posao.id).subscribe(
      poruka=>{
        if(poruka.msg=='ok'){
          alert('Uspesno prihvacen posao')
          this.ngOnInit()
        }
        else {
          alert('Doslo je do greske')
        }
      }
    )
  }

  odbij(posao:Posao){
    if(!this.komentar){
      this.error='Kod odbijanja zahteva morate uneti komentar!'
      return
    }
    this.error=''
    this.servis.dekoraterOdbijZahtev(this.komentar,posao.id).subscribe(
      poruka=>{
        if(poruka.msg=='ok'){
          alert('Uspesno odbijen posao')
          this.ngOnInit()
        }
        else {
          alert('Doslo je do greske')


        }
      }
    )
  }

}
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>
</head>

<body>
    <header>
        <h1>Dobrodošli!</h1>
        <nav>
            <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/dekorater">Profil</a></li>
                <li><a href="/dekorater-odrzavanja">Odrzavanja</a></li>
                <li><a href="/dekorater-zakazivanja">Zakazivanja</a></li>
                <li><a href="/dekorater-statistika">Statistika</a></li>
            </ul>
        </nav>
    </header>

    <main class="main-content">
        <h2>Dostupni poslovi</h2>
        <div class="job-request" *ngFor="let p of posloviZahtev">
            <p>Datum pocetka: {{p.datumPocetka}}</p>
            <p>Datum kraja: {{p.datumKraja}}</p>
            <p>Status: {{p.status}}</p>
            <p>Id:{{p.id}}</p>
            <button class="action-button" (click)="prihvati(p)">Prihvati posao?</button>
            <button class="action-button" (click)="odbij(p)">Odbij posao?</button>
            <input type="text" placeholder="Komentar" [(ngModel)]="komentar">
            <div *ngIf="error">
                {{error}}
            </div>
        </div>
    </main>
    <main class="main-content">
        <h2>Zavrseni poslovi dekoratera</h2>
        <div class="job-request" *ngFor="let p of posloviZahtev">
            <p>Datum pocetka: {{p.datumPocetka}}</p>
            <p>Datum kraja: {{p.datumKraja}}</p>
            <p>Status: {{p.status}}</p>
            <p>Id:{{p.id}}</p>
        </div>
    </main>

    <footer>
        <p>&copy; 2024 Vaša bašta. Sva prava zadržana.</p>
    </footer>
</body>
</html>
