import { Component, OnInit } from '@angular/core';
import { Chart } from 'chart.js';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-dekorater-statistika',
  templateUrl: './dekorater-statistika.component.html',
  styleUrls: ['./dekorater-statistika.component.css']
})
export class DekoraterStatistikaComponent implements OnInit{
  constructor(private servis:UserService,private ruter:Router){

  }

  ngOnInit(): void {
    this.createChart();

  }

  public jobsPerMonth: number[] = [10, 15, 5, 20, 12, 18, 9, 15, 10, 8, 14, 16]; // Primer podataka za svaki mesec


  createChart() {
    const canvas = document.getElementById('myChart') as HTMLCanvasElement;
    const ctx = canvas.getContext('2d');

    if (ctx) { // Proveri da li je ctx validan
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: [
            'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 
            'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'
          ],
          datasets: [{
            label: 'Broj poslova',
            data: this.jobsPerMonth,
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1
          }]
        },
        options: {
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });
    } else {
      console.error('Failed to get canvas context.');
    }
  }

}
