import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { Firma } from '../models/firma';
import Dekorater from '../models/dekorater';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  constructor(private servis:UserService,private ruter:Router){}

  dekorisaneBaste:number=0
  vlasnici:number=0
  dekorateri:number=0
  poslovi24:number=0
  poslovi7:number=0
  poslovi30:number=0
  firme:Firma[]=[]
  slike:string[]=[]
  dekorateri2:Dekorater[]=[]
  backendUrl: string = 'http://localhost:4000/';




  selectedSortOption:string=''
  searchName:string=''
  searchAddress:string=''
  rastuce:boolean=false;
  opadajuce:boolean=false;
  rastuceAdr:boolean=false;
  opadajuceAdr:boolean=false;

  pretragaNaziv:boolean=false;
  pretragaAdresa:boolean=false;
  pretragaOba:boolean=false;





  sortirajRastucePoNazivu() {
    this.firme.sort((a, b) => a.naziv.localeCompare(b.naziv));
  }

  sortirajOpadajucePoNazivu() {
    this.firme.sort((a, b) => b.naziv.localeCompare(a.naziv));
  }

  sortirajRastucePoAdresi(){
    this.firme.sort((a,b)=>a.adresa.localeCompare(b.adresa))
  }

  sortirajOpadajucePoAdresi(){
    this.firme.sort((a,b)=>b.adresa.localeCompare(a.adresa))
  }

  pretraziNaziv() {
    this.firme = this.firme.filter(firma =>
      firma.naziv.toLowerCase().includes(this.searchName.toLowerCase())
    );
  }

  pretraziAdresa() {
    this.firme = this.firme.filter(firma =>
      firma.adresa.toLowerCase().includes(this.searchAddress.toLowerCase())
    );
  }

  pretraziOba() {
    this.firme = this.firme.filter(firma =>
      firma.naziv.toLowerCase().includes(this.searchName.toLowerCase()) &&
      firma.adresa.toLowerCase().includes(this.searchAddress.toLowerCase())
    );
  }

  rastuceSort(){
    this.rastuce=true
    this.ngOnInit()
  }

  opadajuceSort(){
    this.opadajuce=true
    this.ngOnInit()
  }

  rastuceSortAdr(){
    this.rastuceAdr=true
    this.ngOnInit()
  }

  opadajuceSortAdr(){
    this.opadajuceAdr=true
    this.ngOnInit()
  }

  searchIme(){
    this.pretragaNaziv=true
    this.ngOnInit()
  }

  searchAdresa(){
    this.pretragaAdresa=true
    this.ngOnInit()
  }

  searchOba(){
    this.pretragaOba=true
    this.ngOnInit()
  }

  sortiraj(){
    if(!this.selectedSortOption) return
    if(this.selectedSortOption=='asc') {
      this.rastuceSort()
      return; }

    if(this.selectedSortOption=='desc') {
      this.opadajuceSort()
      return
    } 
    if(this.selectedSortOption=='ascAdr'){
      this.rastuceSortAdr()
      return
    }
    if(this.selectedSortOption=='descAdr'){
      this.opadajuceSortAdr()
      return
    }

  }

  restartuj(){
    this.ngOnInit()
  }


  pretrazi(){
    if(this.searchAddress && this.searchName) {
      this.searchOba()
      return
    }
    if(this.searchAddress) {
      this.searchAdresa()
      return
    }
    if(this.searchName) {
      this.searchIme()
    }
  }














  ngOnInit(): void {
      this.servis.pocetnaBrojBasta().subscribe(
        b=>{
          this.dekorisaneBaste=b
        }
      )

      this.servis.pocetnaBrojDekoratera().subscribe(
        b=>{
          this.dekorateri=b
        }
      )

      this.servis.pocetnaBrojVlasnika().subscribe(
        b=>{
          this.vlasnici=b
        }
      )

      this.servis.zakazaniPoslovi7().subscribe(
        p=>{
          this.poslovi7=p
        }
      )

      this.servis.zakazaniPoslovi24().subscribe(
        p=>{
          this.poslovi24=p
        }
      )

      this.servis.zakazaniPoslovi30().subscribe(
        p=>{
          this.poslovi30=p
        }
      )
      this.servis.dohvatiFirme().subscribe(
        f=>{
          this.firme=f
        }
      )

      this.servis.dohvatiDekoratere().subscribe(
        d=>{
          this.dekorateri2=d
        }
      )

      this.servis.poslednjeSlike().subscribe(
        d=>{
          this.slike=d
        }
      )
  }

  getUserImageUrl(imagePath: string): string {
    return this.backendUrl + imagePath;
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>
</head>
<body>
    <header>
        <h1>Vaša Bašta Vaša Mašta</h1>
        <nav>
            <ul>
                <li><a href="">Home</a></li>
                <li><a href="/praviLogin">Prijava</a></li>
                <li><a href="/login-vlasnik">Registracija</a></li>
                <li><a href="/promena-lozinke">Promena Lozinke</a></li>
            </ul>
        </nav>
    </header>

    <section class="info">
        <h2>Opšte Informacije</h2>
        <div class="stats">
            <div class="stat-item">
                <h3>Dekorisane Bašte</h3>
                <p id="decoratedGardens">{{dekorisaneBaste}}</p>
            </div>
            <div class="stat-item">
                <h3>Registrovanje Vlasnici</h3>
                <p id="registeredOwners">{{vlasnici}}</p>
            </div>
            <div class="stat-item">
                <h3>Registrovanje Dekoratera</h3>
                <p id="registeredDecorators">{{dekorateri}}</p>
            </div>
            <div class="stat-item">
                <h3>Poslovi (last 24h)</h3>
                <p id="jobs24h">{{poslovi24}}</p>
            </div>
            <div class="stat-item">
                <h3>Poslovi (last 7 days)</h3>
                <p id="jobs7d">{{poslovi7}}</p>
            </div>
            <div class="stat-item">
                <h3>Poslovi (last 30 days)</h3>
                <p id="jobs30d">{{poslovi30}}</p>
            </div>
        </div>
    </section>
    <div>
        <label for="sortOptions" >Sortiraj:</label>
        <select id="sortOptions" [(ngModel)]="selectedSortOption">
            <option value="asc">Sortiraj rastuće(po nazivu)</option>
            <option value="desc">Sortiraj opadajuće(po nazivu)</option>
            <option value="ascAdr">Sortiraj rastuce(po adresi)</option>
            <option value="descAdr">Sortiraj opadajuce(po adresi)</option>
        </select>
        <button (click)="sortiraj()">Sortiraj</button>
    </div>

    <div >
        <div >
            <label for="searchByName">Pretrazi po nazivu:</label>
            <input type="text" id="searchByName" [(ngModel)]="searchName" placeholder="Unesite naziv">
        </div>
        <div class="search-field">
            <label for="searchByAddress">Pretrazi po adresi:</label>
            <input type="text" id="searchByAddress" [(ngModel)]="searchAddress" placeholder="Unesite adresu">
        </div>
        <button (click)="pretrazi()">Pretraži</button>
        <button (click)="restartuj()">Restartuj</button>
    </div>

    <section class="companies">
        <h2>Firme i Dekorateri</h2>
        <div class="company-list">
            <div *ngFor="let f of firme" class="company-item">
                <h3>{{f.naziv}}</h3>
                <div *ngFor="let dekorater of dekorateri2" [hidden]="dekorater.firma != f.naziv" class="dekorater-item">
                    <p class="dekorater-ime">{{dekorater.ime}} {{dekorater.prezime}}</p>
                </div>
            </div>
        </div>
    </section>

    <section class="gallery">
        <h2>Galerija Završenih Poslova</h2>
        <div class="gallery-images" id="gallery">
            <div *ngFor="let slika of slike" class="gallery-item">
                <img [src]="getUserImageUrl(slika)" alt="Profilna slika korisnika" class="profile-image">
            </div>
        </div>
    </section>

    <footer>
        <p>&copy; 2024 Vaša bašta. Sva prava zadržana.</p>
    </footer>
</body>
</html>
