import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { Firma } from '../models/firma';
import { Usluga } from '../models/usluga';

@Component({
  selector: 'app-firma',
  templateUrl: './firma.component.html',
  styleUrls: ['./firma.component.css']
})
export class FirmaComponent implements OnInit {
  adresa: string = '';
  naziv:string=''
  map: any
  marker: L.Marker | undefined;
  cena:number=0;
  usluga:string=''
  error:string=''
  datum_od:Date=new Date()
  datum_do:Date=new Date()

  firma:Firma=new Firma()
  usluge:Usluga[]=[]

  constructor() {}

  ngOnInit(): void {
    this.inicijalizujMapu();
  }

  inicijalizujMapu(): void {
    this.map = L.map('map').setView([51.673858, 7.815982], 10); // Podrazumevana lokacija

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(this.map);
  }

  pretraziAdresu(): void {
    if (!this.adresa) return;

    const url = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(this.adresa)}&format=json&limit=1`;

    fetch(url)
      .then(response => response.json())
      .then(data => {
        if (data.length > 0) {
          const lat = parseFloat(data[0].lat);
          const lon = parseFloat(data[0].lon);

          if (this.map && this.marker) {
            this.map.removeLayer(this.marker); // Ukloni prethodni marker ako postoji
          }

          this.map.setView([lat, lon], 15); // Prikazivanje mape sa novom lokacijom

          this.marker = L.marker([lat, lon]).addTo(this.map);
          this.marker.bindPopup(`<b>${this.adresa}</b>`).openPopup();
        } else {
          alert('Adresa nije pronađena.');
        }
      })
      .catch(error => {
        console.error('Greška prilikom geokodiranja:', error);
        alert('Greška prilikom geokodiranja.');
      });
  }

  dodajUslugu(){
    if(!this.usluga || !this.cena) {
      this.error='Niste uneli potrebne podatke!'
    } else {
      let nova:Usluga=new Usluga()
      nova.naziv=this.usluga
      nova.cena=this.cena
      this.usluge.push(nova)
    }
  }

  saljiPodatke(){

  }

}

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title></title>

</head>
<body>
  <header>

  </header>

<div class="container">
<table>
  <tr>
    <td>
      Naziv
    </td>
    <td>
      <input type="text" [(ngModel)]="naziv" placeholder="Unesite naziv">
    </td>
  </tr>
  <tr>
    <td>
      Naziv usluge:
    </td>
    <td>
      <input type="text" [(ngModel)]="usluga" placeholder="Unesite uslugu">
    </td>
  <tr>
    <td>
      Cena usluge:
    </td>
    <td>
      <input type="text" [(ngModel)]="cena" placeholder="Unesite cenu">
    </td>
    <td>
      <button (click)="dodajUslugu()">Dodajte uslugu</button>
    </td>
  </tr>

  <tr>
    <td>
      <div class="error">
        {{error}}
      </div>
    </td>
  </tr>

  <tr>
    <td>
      Pocetak godisnjeg odmora:
    </td>
    <td>
      <input type="date" [(ngModel)]="datum_od" placeholder="Izaberite datum">
    </td>
  </tr>
  <tr>
    <td>
      Kraj godisnjeg odmora
    </td>
    <td>
      <input type="date" [(ngModel)]="datum_do" placeholder="Izaberite datum">

    </td>
  </tr>

  <tr>

    <td>Adresa:</td>
    <td>
      <input type="text" [(ngModel)]="adresa" placeholder="Unesite adresu">
    </td>
    <td>
      <button (click)="pretraziAdresu()">Prikaži na mapi</button>
    </td>
  </tr>
  <tr>
    <td colspan="2">
      <div class="container2">
        <div id="map"></div>
      </div>
    </td>
  </tr>
</table>
</div>

<footer>
  <p>&copy; 2024 Vaša bašta.Sva prava zadržana.</p>
</footer>
</body>
