import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { Firma } from '../models/firma';
import Dekorater from '../models/dekorater';

@Component({
  selector: 'app-firme',
  templateUrl: './firme.component.html',
  styleUrls: ['./firme.component.css']
})
export class FirmeComponent implements OnInit {
  constructor(private servis:UserService,private ruter:Router){}

  ngOnInit(): void {
      this.servis.dohvatiFirme().subscribe(
        f=>{
          this.firme=f

          if(this.rastuce) {
            this.sortirajRastucePoNazivu()
            this.rastuce=false
          }
          if(this.opadajuce){
            this.sortirajOpadajucePoNazivu()
            this.opadajuce=false
          }
          if(this.rastuceAdr){
            this.sortirajRastucePoAdresi()
            this.rastuceAdr=false
          }
          if(this.opadajuceAdr){
            this.sortirajOpadajucePoAdresi()
            this.opadajuceAdr=false
          }
          if(this.pretragaOba){
            this.pretraziOba()
            this.pretragaOba=false
          }
          if(this.pretragaNaziv){
            this.pretraziNaziv()
            this.pretragaNaziv=false
          }
          if(this.pretragaAdresa){
            this.pretraziAdresa()
            this.pretragaAdresa=false
          }
        }
      )
      this.servis.dohvatiDekoratere().subscribe(
        d=>{
          this.dekorateri=d
        }
      )

  }

  menuVisible = false; // Kontrola vidljivosti padajućeg menija
  firme:Firma[]=[]
  dekorateri:Dekorater[]=[]
  selectedSortOption:string=''
  searchName:string=''
  searchAddress:string=''
  rastuce:boolean=false;
  opadajuce:boolean=false;
  rastuceAdr:boolean=false;
  opadajuceAdr:boolean=false;

  pretragaNaziv:boolean=false;
  pretragaAdresa:boolean=false;
  pretragaOba:boolean=false;




  sortirajRastucePoNazivu() {
    this.firme.sort((a, b) => a.naziv.localeCompare(b.naziv));
  }

  sortirajOpadajucePoNazivu() {
    this.firme.sort((a, b) => b.naziv.localeCompare(a.naziv));
  }

  sortirajRastucePoAdresi(){
    this.firme.sort((a,b)=>a.adresa.localeCompare(b.adresa))
  }

  sortirajOpadajucePoAdresi(){
    this.firme.sort((a,b)=>b.adresa.localeCompare(a.adresa))
  }

  pretraziNaziv() {
    this.firme=this.firme.filter(firma=>firma.naziv.toLowerCase()===this.searchName.toLowerCase())
  }

  pretraziAdresa(){
    this.firme=this.firme.filter(firma=>firma.adresa.toLowerCase()===this.searchAddress.toLowerCase())
  }

  pretraziOba(){
    this.firme=this.firme.filter(firma=>firma.naziv.toLowerCase()===this.searchName.toLowerCase()
  && firma.adresa.toLowerCase()===this.searchAddress.toLowerCase())
  }

  rastuceSort(){
    this.rastuce=true
    this.ngOnInit()
  }

  opadajuceSort(){
    this.opadajuce=true
    this.ngOnInit()
  }

  rastuceSortAdr(){
    this.rastuceAdr=true
    this.ngOnInit()
  }

  opadajuceSortAdr(){
    this.opadajuceAdr=true
    this.ngOnInit()
  }

  searchIme(){
    this.pretragaNaziv=true
    this.ngOnInit()
  }

  searchAdresa(){
    this.pretragaAdresa=true
    this.ngOnInit()
  }

  searchOba(){
    this.pretragaOba=true
    this.ngOnInit()
  }

  sortiraj(){
    if(!this.selectedSortOption) return
    if(this.selectedSortOption=='asc') {
      this.rastuceSort()
      return; }

    if(this.selectedSortOption=='desc') {
      this.opadajuceSort()
      return
    } 
    if(this.selectedSortOption=='ascAdr'){
      this.rastuceSortAdr()
      return
    }
    if(this.selectedSortOption=='descAdr'){
      this.opadajuceSortAdr()
      return
    }

  }




  pretrazi(){
    if(this.searchAddress && this.searchName) {
      this.searchOba()
      return
    }
    if(this.searchAddress) {
      this.searchAdresa()
      return
    }
    if(this.searchName) {
      this.searchIme()
    }
  }
}

<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>

</head>

<body>
    <header>
        <h1>Dobrodošli!</h1>
        <nav>
            <ul>
                <li><a href="/vlasnik">Profil</a></li>
                <li><a href="/firme">Firme</a></li>
                <li><a href="/promena-lozinke">Zakazivanja</a></li>
                <li><a href="/promena-lozinke">Održavanja</a></li>
            </ul>
        </nav>
    </header>

    <div class="main-content">
        <div class="dropdown-container">
            <label for="sortOptions" class="dropdown-label">Sortiraj:</label>
            <select id="sortOptions" [(ngModel)]="selectedSortOption">
                <option value="asc">Sortiraj rastuće(po nazivu)</option>
                <option value="desc">Sortiraj opadajuće(po nazivu)</option>
                <option value="ascAdr">Sortiraj rastuce(po adresi)</option>
                <option value="descAdr">Sortiraj opadajuce(po adresi)</option>
            </select>
            <button (click)="sortiraj()">Sortiraj</button>
        </div>

        <div class="search-container">
            <div class="search-field">
                <label for="searchByName">Pretrazi po nazivu:</label>
                <input type="text" id="searchByName" [(ngModel)]="searchName" placeholder="Unesite naziv">
            </div>
            <div class="search-field">
                <label for="searchByAddress">Pretrazi po adresi:</label>
                <input type="text" id="searchByAddress" [(ngModel)]="searchAddress" placeholder="Unesite adresu">
            </div>
            <button (click)="pretrazi()">Pretraži</button>
        </div>

        <div class="firma-container">
            <div *ngFor="let firma of firme" class="firma-card">
                <div class="firma-info">
                    <h2>{{firma.naziv}}</h2>
                    <p class="firma-adresa">{{firma.adresa}}</p>
                </div>
                <div class="dekorateri-list">
                    <div *ngFor="let dekorater of dekorateri" [hidden]="dekorater.firma != firma.naziv" class="dekorater-item">
                        <p class="dekorater-ime">{{dekorater.ime}} {{dekorater.prezime}}</p>
                    </div>
                </div>
            </div>
        </div>
    </div>

    <footer>
        <p>&copy; 2024 Vaša bašta. Sva prava zadržana.</p>
    </footer>
</body>

</html>
