import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { Firma } from '../models/firma';
import * as L from 'leaflet';


@Component({
  selector: 'app-detalji-firme',
  templateUrl: './detalji-firme.component.html',
  styleUrls: ['./detalji-firme.component.css']
})
export class DetaljiFirmeComponent implements OnInit {
  constructor(private servis:UserService,private ruter:Router){}

  ngOnInit(): void {
    this.inicijalizujMapu()

      let naziv=localStorage.getItem('firmica')
      naziv=naziv==null?'':naziv
      this.naziv=naziv
      this.servis.getFirma(naziv).subscribe(
        f=>{
          this.firma=f
          this.pretraziAdresu()
        }
      )

  }
  naziv:string=''
  firma:Firma=new Firma()
  map: any
  marker: L.Marker | undefined;



  inicijalizujMapu(): void {
    this.map = L.map('map').setView([51.673858, 7.815982], 10); // Podrazumevana lokacija

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(this.map);
  }




  pretraziAdresu(): void {
    if (!this.firma.adresa) return;

    const url = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(this.firma.adresa)}&format=json&limit=1`;

    fetch(url)
      .then(response => response.json())
      .then(data => {
        if (data.length > 0) {
          const lat = parseFloat(data[0].lat);
          const lon = parseFloat(data[0].lon);

          if (this.map && this.marker) {
            this.map.removeLayer(this.marker); // Ukloni prethodni marker ako postoji
          }

          this.map.setView([lat, lon], 15); // Prikazivanje mape sa novom lokacijom

          this.marker = L.marker([lat, lon]).addTo(this.map);
          this.marker.bindPopup(`<b>${this.firma.adresa}</b>`).openPopup();
        } else {
          alert('Adresa nije pronađena.');
        }
      })
      .catch(error => {
        console.error('Greška prilikom geokodiranja:', error);
        alert('Greška prilikom geokodiranja.');
      });
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>

</head>
<body>
    <header>
        <h1>Dobrodošli!</h1>
        <nav>
            <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/vlasnik">Profil</a></li>
                <li><a href="/firme">Firme</a></li>
                <li><a href="/promena-lozinke">Zakazivanja</a></li>
                <li><a href="/promena-lozinke">Odrzavanja</a></li>
            </ul>
        </nav>
    </header>

    <div class="main-content">
        <section class="firma-details">
            <h2>{{firma.naziv}}</h2>
            <p class="firma-adresa">{{firma.adresa}}</p>
            <p class="firma-kontakt">{{firma.kontakt}}</p>
        </section>

        <section class="map-container">
            <div id="map"></div>
        </section>

        <section class="usluge-list">
            <h3>Usluge</h3>
            <div *ngFor="let usluga of firma.usluge" class="usluga-item">
                <p class="usluga-naziv">{{usluga.naziv}}</p>
                <p class="usluga-cena">{{usluga.cena}}</p>
            </div>
        </section>
        Zakazivanje(step1)
        <table>
            <tr>
                <td>
                    Datum izrade
                </td>
                <td>
                    <input type="date" [(ngModel)]="datumPocetka">
                </td>
            </tr>
            <tr>
                <td>
                    Datum zavrsetka
                </td>
                <td>
                    <input type="date" [(ngModel)]="datumKraja">
                </td>
            </tr>
            <tr>
                <td>
                    Kvadratura baste
                </td>
                <td>
                    <input type="number" [(ngModel)]="kvadraturaBaste">
                </td>
            </tr>
            <tr>
                <td>
                    Tip baste:
                </td>
                <td>
                    <select name="basta" [(ngModel)]="tip">
                        <option value="basta"> privatna basta</option>
                        <option value="restoran"> restoran</option>
                    </select>
                </td>
            </tr>

        </table>
    </div>

    <footer>
        <p>&copy; 2024 Vaša bašta. Sva prava zadržana.</p>
    </footer>
</body>
</html>
