  //     status:status
  //   }

  //   return this.http.post<Poruka>(`${this.uri}/saveVlasnik`, data)


  //}
  saveVlasnik(vlasnik:FormData){
       return this.http.post<Poruka>(`${this.uri}/saveVlasnik`, vlasnik)

  }

  getUsers(){
    return this.http.post<string[]>(`${this.uri}/getUsers`, {'nista':'nista'})

  }
}
