import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-promena-lozinke',
  templateUrl: './promena-lozinke.component.html',
  styleUrls: ['./promena-lozinke.component.css']
})
export class PromenaLozinkeComponent {

  constructor(private http: HttpClient, private ruter: Router, private servis: UserService) { }

  lozinka: string = ''
  staraLozinka: string = ''
  ponovljena: string = ''
  validnastaraLozinka: boolean = false
  validnaLozinka: boolean = false
  validnaPonovljena: boolean = false
  error:string=''


  lozinkaRegex = /^(?=[A-Za-z])(?=.*[a-z].*[a-z].*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#\$%\^\&*\)\(+=._-]).{6,10}$/;



  proveriLozinku1(){
    this.validnastaraLozinka = this.lozinkaRegex.test(this.staraLozinka)
  }

  proveriLozinku2(){
    this.validnaLozinka = this.lozinkaRegex.test(this.lozinka)
  }

  proveriLozinku3(){
    this.validnaPonovljena = this.lozinkaRegex.test(this.ponovljena)

  }

  changePassword(){
    if(this.lozinka!=this.ponovljena) {this.error="Nova i ponovljena lozinka nisu iste!"
      return;
    } else{
      this.servis.changePassword(this.lozinka,this.staraLozinka).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.error="Lozinka je uspesno promenjena!"}
          else {
            if(poruka.msg=='Ne') {this.error='Ne postoji korisnik sa datom lozinkom!!!'}
            else {this.error="Doslo je do greske u sistemu prilikom promene lozinke"}
          }
        }
      )
    }
  }

  logout(){
    localStorage.clear
    this.ruter.navigate([''])
  }





}
<div class="container">
<table>
    <tr>
        <td>
            Stara lozinka:
        </td>
        <td>
            <input type="text" [(ngModel)]="staraLozinka" name="staraLozinka" (ngModelChange)="proveriLozinku1()">
        </td>
    </tr>
    <tr *ngIf="!validnastaraLozinka && staraLozinka">
        <td colspan="2" class="error-message">Lozinka mora biti u formatu: najmanje 6,
             najviše 10 karaktera, jedno veliko slovo, tri mala slova
             , jedan broj i jedan specijalni karakter, i mora počinjati slovom.</td>
    </tr>
    <tr>
        <td>
            Nova lozinka:
        </td>
        <td>
            <input type="text" [(ngModel)]="lozinka" name="lozinka" (ngModelChange)="proveriLozinku2()">
        </td>
    </tr>
    <tr *ngIf="!validnaLozinka && lozinka">
        <td colspan="2" class="error-message">Lozinka mora biti u formatu: najmanje 6,
             najviše 10 karaktera, jedno veliko slovo, tri mala slova
             , jedan broj i jedan specijalni karakter, i mora počinjati slovom.</td>
    </tr>
    <tr>
        <td>
            Ponovi lozinku:
        </td>
        <td>
            <input type="text" [(ngModel)]="ponovljena" name="ponovljena" (ngModelChange)="proveriLozinku3()">
        </td>
    </tr>
    <tr *ngIf="!validnaPonovljena && ponovljena">
        <td colspan="2" class="error-message">Lozinka mora biti u formatu: najmanje 6,
             najviše 10 karaktera, jedno veliko slovo, tri mala slova
             , jedan broj i jedan specijalni karakter, i mora počinjati slovom.</td>
    </tr>
    <tr>
        <td colspan="2">
            {{error}}
        </td>
    </tr>
    <tr>
        <td colspan="2">
            <button (click)="changePassword()">CHANGE PASSWORD</button>
        </td>
    </tr>
    <tr>
        <td colspan="2">
            <button (click)="logout()">HOME</button>
        </td>
    </tr>


</table>
</div>
