import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import Vlasnik from '../models/vlasnik';

@Component({
  selector: 'app-admin2',
  templateUrl: './admin2.component.html',
  styleUrls: ['./admin2.component.css']
})
export class Admin2Component implements OnInit {

  constructor(private servis:UserService,private ruter:Router){}

  vlasnici:Vlasnik[]=[]

  ngOnInit(): void {
      this.servis.zahteviObrada().subscribe(
        v=>{
          this.vlasnici=v
        }
      )
  }

  prihvati(korisnicko_ime:string){
    this.servis.prihvatiZahtev(korisnicko_ime).subscribe(
      poruka=>{
        if(poruka.msg=='ok') {this.ngOnInit()}
        else {
          alert('Neuspesno prihvatanje zahteva')
        }
      }
    )
  }

  odbij(korisnicko_ime:string){

    this.servis.odbijZahtev(korisnicko_ime).subscribe(
      poruka=>{
        if(poruka.msg=='ok') {this.ngOnInit()}
        else {alert('Neuspeno odbijanje zahteva')}
      }
    )

  }


}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>

</head>
<body>
    <header>
        <h1>Administratore,dobrodošli</h1>
        <nav>
            <ul>
                <li><a href="/administrator">login</a></li>
                <li><a href="/">home</a></li>
                <li><a href="/praviLogin">spiskovi</a></li>
                <li><a href="/login-vlasnik">zahtevi</a></li>
                <li><a href="/promena-lozinke">dodavanje dekoratera</a></li>
                <li><a href="/promena-lozinke">dodavanje firmi</a></li>

            </ul>
        </nav>
    </header>
    <table>
        <tr>
            <td>
                Korisnicko ime:
            </td>
            <td>
                Ime:
            </td>
            <td>
                Prezime:
            </td>
        </tr>
        <tr *ngFor="let vlasnik of vlasnici;let i=index">
            <td>
                {{vlasnik.korisnicko_ime}}
            </td>
            <td>
                {{vlasnik.ime}}
            </td>
            <td>
                {{vlasnik.prezime}}
            </td>
            <td>
                <button (click)="prihvati(vlasnik.korisnicko_ime)">prihvati</button>
            </td>
            <td>
                <button (click)="odbij(vlasnik.korisnicko_ime)">odbij</button>
            </td>

    </table>
    <footer>
        <p>&copy; 2024 Vaša bašta.Sva prava zadržana.</p>
    </footer>
</body>
