<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>

</head>
<body>
    <header>
        <h1>Administratore,dobrodošli</h1>
        <nav>
            <ul>
                <li><a href="/administrator">login</a></li>
                <li><a href="/">home</a></li>
                <li><a href="/spiskovi">spiskovi</a></li>
                <li><a href="/admin2">zahtevi</a></li>
                <li><a href="/promena-lozinke">dodavanje dekoratera</a></li>
                <li><a href="/firma">dodavanje firmi</a></li>

            </ul>
        </nav>
    </header>
    <div class="center-content">
        <div class="container">
            <table>
                <tr>
                    <th>
                        Korisnicko ime
                    </th>

                    <th>
                        Ime
                    </th>
                    <th>
                       Prezime
                    </th>
                    <th>
                        Adresa
                    </th>
                    <th>
                        Telefon
                    </th>
                    <th>
                        Slika
                    </th>
                    <th>
                        Kartica
                    </th>
                    <th>
                        Status
                    </th>
                </tr>
                <tr>
                    <td>
                        {{vlasnik.korisnicko_ime}}
                    </td>

                    <td>
                        {{vlasnik.ime}}
                    </td>
                    <td>
                        {{vlasnik.prezime}}
                    </td>
                    <td>
                        {{vlasnik.adresa}}
                    </td>
                    <td>
                        {{vlasnik.telefon}}
                    </td>
                    <td>
                        <img [src]="getUserImageUrl(vlasnik.slika)" alt="Profilna slika korisnika">
                    </td>
                    <td>
                        {{vlasnik.kartica}}
                    </td>
                    <td>
                        {{vlasnik.status}}
                    </td>
                </tr>
            </table>
        </div>
        <div class="container">
            <table>
                <tr>
                    <td>
                        <input type="text" [(ngModel)]="ime" name="ime">
                    </td>
                    <td>
                        <button (click)="promeniIme()">Promeni ime?</button>
                    </td>  
                </tr>
                <tr>
                    <td>
                        <input type="text" [(ngModel)]="prezime" name="prezime">
                    </td>
                    <td>
                        <button (click)="promeniPrezime()">Promeni prezime?</button>
