import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import Vlasnik from '../models/vlasnik';

@Component({
  selector: 'app-vlasnik',
  templateUrl: './vlasnik.component.html',
  styleUrls: ['./vlasnik.component.css']
})
export class VlasnikComponent implements OnInit {

    constructor(private servis:UserService,private ruter:Router){}

    vlasnik:Vlasnik=new Vlasnik()

    backendUrl: string = 'http://localhost:4000/';


    dinersRegex = /^(300|301|302|303|36|38)\d{12}$/;

    masterCardRegex = /^(51|52|53|54|55)\d{14}$/;


    visaRegex = /^(4539|4556|4916|4532|4929|4485|4716)\d{12}$/;

    ime:string=''
    prezime:string=''
    broj:string=''
    imejl:string=''
    picture: File | null = null
    kartica:number=0;

    error:string=''

    onFileSelected(event: any): void {
      const file: File = event.target.files[0];
      if (file && (file.type === 'image/jpeg' || file.type === 'image/png')) {
        this.picture = file;  // Čuva sliku ako je u pravilnom formatu
      } else {
        alert("Samo JPG/PNG formati su podržani!");
      }
    }

    promeniSliku(){
      const formData = new FormData();
      formData.append('korisnicko_ime',this.vlasnik.korisnicko_ime)
      if(this.picture)
      formData.append('slika',this.picture)
      this.servis.promeniSliku(formData).subscribe(
        poruka=>{
          if(poruka.msg=='ok'){this.ngOnInit()}
          else{
            alert('Nije uspelo azuziranje slike')
          }
        }
      )

    }

    promeniIme(){
      this.servis.promeniIme(this.vlasnik.korisnicko_ime,this.ime).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.ngOnInit()}
          else{ alert('Azuriranje imena nije uspelo')}
        }
      )
    }

    promeniPrezime(){
      this.servis.promeniPrezime(this.vlasnik.korisnicko_ime,this.prezime).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.ngOnInit()}
          else{ alert('Azuriranje prezimena nije uspelo')}
        }
      )
    }

    promeniMejl(){
      this.servis.promeniMejl(this.vlasnik.korisnicko_ime,this.imejl).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.ngOnInit()}
          else{ alert('Azuriranje mejla nije uspelo')}
        }
      )
    }

    promeniBroj(){
      this.servis.promeniBroj(this.vlasnik.korisnicko_ime,this.broj).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.ngOnInit()}
          else{ alert('Azuriranje broja nije uspelo')}
        }
      )
    }


    ngOnInit(): void {
        let naziv=localStorage.getItem('ulogovan')
        naziv=naziv==null?'':naziv
        this.servis.getVlasnik(naziv).subscribe(
          korisnik=>{
            this.vlasnik=korisnik
          }
        )
    }

    getUserImageUrl(imagePath: string): string {
      // Kombinujte osnovni URL sa putanjom iz baze
      return this.backendUrl + imagePath;
    }

    promeniKarticu(){
      let proveraKartice=this.kartica.toString()
      if(!this.dinersRegex.test(proveraKartice) && !this.masterCardRegex.test(proveraKartice) && !this.visaRegex.test(proveraKartice))
      {
        this.error='Broj kartice nije u odgovarajucem formatu!'
      }
      this.servis.promeniKarticu(this.vlasnik.korisnicko_ime,this.kartica).subscribe(
        poruka=>{
          if(poruka.msg=='ok') {this.ngOnInit()}
          else{ alert('Azuriranje kartice nije uspelo')}
        }
      )
    }
  }

<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Početna Stranica</title>
</head>

<body>
    <header>
        <h1>Dobrodošli!</h1>
        <nav>
            <ul>
                <li><a href="/">Home</a></li>
                <li><a href="/vlasnik">Profil</a></li>
                <li><a href="/firme">Firme</a></li>
                <li><a href="/zakazivanja">Zakazivanja</a></li>
                <li><a href="/odrzavanje">Odrzavanja</a></li>
            </ul>
        </nav>
    </header>
    <main class="main-content">
        <h2>Profil Vlasnika</h2>
        <div class="container">
            <table class="data-table">
                <tr>
                    <td>Ime:</td>
                    <td>{{vlasnik.ime}}</td>
                </tr>
                <tr>
                    <td>Prezime:</td>
                    <td>{{vlasnik.prezime}}</td>
                </tr>
                <tr>
                    <td>Mejl:</td>
                    <td>{{vlasnik.imejl}}</td>
                </tr>
                <tr>
                    <td>Kontakt:</td>
                    <td>{{vlasnik.telefon}}</td>
                </tr>
                <tr>
                    <td>Broj kartice:</td>
                    <td>{{vlasnik.kartica}}</td>
                </tr>
                <tr>
                    <td>Fotografija:</td>
                    <td>
                        <img [src]="getUserImageUrl(dekorater.slika)" alt="Profilna slika korisnika" class="profile-image">
                    </td>
                </tr>
            </table>
        </div>
        <div class="container">
            <h2>Promena podataka</h2>
            <table class="data-table">
                <tr>
                    <td><input type="text" [(ngModel)]="ime" name="ime" placeholder="Novo ime"></td>
                    <td><button class="action-button" (click)="promeniIme()">Promeni ime?</button></td>
                </tr>
                <tr>
                    <td><input type="text" [(ngModel)]="prezime" name="prezime" placeholder="Novo prezime"></td>
                    <td><button class="action-button" (click)="promeniPrezime()">Promeni prezime?</button></td>
                </tr>
                <tr>
                    <td><input type="text" [(ngModel)]="imejl" name="imejl" placeholder="Novi imejl"></td>
                    <td><button class="action-button" (click)="promeniMejl()">Promeni imejl?</button></td>
                </tr>
                <tr>
                    <td><input type="text" [(ngModel)]="broj" name="broj" placeholder="Novi broj"></td>
                    <td><button class="action-button" (click)="promeniBroj()">Promeni broj?</button></td>
                </tr>
                <tr>
                    <td><input type="number" [(ngModel)]="kartica" name="kartica" placeholder="Nova kartica"></td>
                    <td><button class="action-button" (click)="promeniKarticu()">Promeni karticu?</button></td>
                </tr>
                <tr>
                    <td colspan="2"><p class="error-message">{{error}}</p></td>
                </tr>
                <tr>
                    <td><input type="file" name="profilna_slika" (change)="onFileSelected($event)" class="input-field"></td>
                    <td><button class="action-button" (click)="promeniSliku()">Promeni sliku?</button></td>
                </tr>
            </table>
        </div>
