import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import Vlasnik from '../models/vlasnik';

@Component({
  selector: 'app-vlasnik',
  templateUrl: './vlasnik.component.html',
  styleUrls: ['./vlasnik.component.css']
})
export class VlasnikComponent implements OnInit {

    constructor(private servis:UserService,private ruter:Router,private http:HttpClient){}

    vlasnik:Vlasnik=new Vlasnik()

    ngOnInit(): void {
        let naziv=localStorage.getItem('ulogovan')
        naziv=naziv==null?'':naziv
        this.servis.getVlasnik(naziv).subscribe(
          korisnik=>{
            this.vlasnik=korisnik
          }
        )
    }
}
